def mult(a,b,m):
    n1,m1=len(a),len(a[0])
    n2,m2=len(b),len(b[0])
    if m1!=n2: raise Exception('Dimension error')
    c=[[0]*m2 for i in range(n1)]
    for i in range(m2):
        for j in range(n1):
            for k in range(n2):
                c[j][i]=(c[j][i]+b[k][i]*a[j][k])%m
    return c

def matExp(a,b,m):
    res=[[int(i==j) for j in range(len(a))] for i in range(len(a))]
    while b:
        if b&1: res=mult(res,a,m)
        a=mult(a,a,m)
        b>>=1
    return res


