def solve(s):
    valueToCount = {}
    for value in s:
        valueToCount[value] = valueToCount.get(value, 0) + 1

    result = 1
    max_value = max(s)
    primes = [True] * (max_value + 1)
    for i in range(2, len(primes)):
        size = 0
        if primes[i]:
            for j in range(i, len(primes), i):
                primes[j] = False
                size += valueToCount.get(j, 0)
        
        result = max(result, size)

    return result

