class DisjointSetUnion:
    def __init__(self, n):
        self.p = list(range(n))
        self.rank = [0] * n
        self._size = [1] * n

    def get(self, elm):
        rep = elm
        while self.p[rep] != rep:
            rep = self.p[rep]

        while self.p[elm] != rep:
            self.p[elm], elm = rep, self.p[elm]

        return rep

    def size(self, elm):
        rep = self.get(elm)
        return self._size[rep]

    def union(self, a, b):
        arep = self.get(a)
        brep = self.get(b)

        if arep != brep:
            if self.rank[arep] > self.rank[brep]:
                arep, brep = brep, arep

            if self.rank[arep] == self.rank[brep]:
                self.rank[brep] += 1

            self.p[arep] = brep
            self._size[brep] += self._size[arep]
            self._size[arep] = self.rank[arep] = 0

    def count_ccomps(self):
        return sum(1 for si in self._size if si > 0)


