def trees(array):
	num_trees = 0
	checked = {}
	for i in array:
		if i not in checked:
			new_tree = []
			curr_vert = i
			while curr_vert not in new_tree and curr_vert not in checked:
				new_tree.append(curr_vert)
				curr_vert = array[curr_vert-1]
			if curr_vert in checked:
				for vert in new_tree:
					checked[vert] = checked[curr_vert]
			else:
				num_trees += 1
				for vert in new_tree:
					checked[vert] = num_trees
	return num_trees

