from itertools import accumulate


def handle() -> int:
    n, t = [int(s) for s in input().split(" ")]
    prefix_sum = list(accumulate([0] + [int(s) for s in input().split(" ")]))
    L = 0
    R = n

    if t >= prefix_sum[-1]:
        return n

    def works(value: int) -> bool:
        for i in range(value, n + 1):
            if prefix_sum[i] - prefix_sum[i - value] <= t:
                return True
        return False

    best = 0

    while L <= R:
        mid = (L + R) // 2
        if works(mid):
            best = mid
            L = mid + 1
        else:
            R = mid - 1
    return best


print(handle())

