def encode(parameters: str, message: str, key: str):
    message_length,key_length,capacity = get_int_map(parameters)
    message_values = list(get_int_map(message))
    key_values = list(get_int_map(key))
    for i in range(message_length-key_length+1):
        for j in range(key_length):
            message_values[i+j] = (message_values[i+j]+key_values[j])%capacity
    return " ".join(map(str, message_values))

