def check(s, a, used):
    if len(used) == len(s):
        return True
    n = len(a)
    index = len(used)
    for i in range(n):
        if i not in used and s[index] in a[i]:
            result = check(s, a, used.union(set([i])))
            if result:
                return True
    return False


def valid(s, a):
    if len(s) > len(a):
        return False
    return check(s, a, set())


