s = input()

def get_pairs(s):
    pairs = []
    pair = None

    for letter in s:
        if pair and pair[0] != letter:
            pairs.append(pair)

        if pair and pair[0] == letter:
            pair[1] += 1
        else:
            pair = [letter, 1]

    pairs.append(pair)
    return pairs

def decrease_to_doubles(pairs):
    result = []

    for pair in pairs:
        a, b = pair

        if b > 2:
            b = 2

        result.append([a, b])

    return result

def decrease_to_ones(pairs):
    start = 0
    finish = start + 2

    while finish <= len(pairs):
        a, b = pairs[start:finish]

        if a[1] == 2 and b[1] == 2:
            b[1] = 1

        start+=1
        finish+=1

    return pairs

def restore_word(pairs):
    letters = []
    for pair in pairs:
        letter, multiplier = pair
        letters.append(letter*multiplier)

    result = ''.join(letters)
    return result

