# Magic no recursion
from types import GeneratorType  # noqa
def bootstrap(f, stack=[]):  # noqa
    def wrappedfunc(*args, **kwargs):
        if stack:
            return f(*args, **kwargs)
        to = f(*args, **kwargs)
        while True:
            if type(to) is GeneratorType:
                stack.append(to)
                to = next(to)
            else:
                stack.pop()
                if not stack:
                    break
                to = stack[-1].send(to)
        return to
    return wrappedfunc

def get_int() -> int:
    # This function gets an integer input from the user.
    return int(user_input())

def line_integers() -> list[int]:
    # This function gets a line of integers separated by spaces, finally returning all of them as a list.
    return list(map(int, user_input().split()))

def get_string(as_list: bool = False) -> str | list[str]:
    # This function gets a string from the user and converts it to a list of characters.
    input_string = user_input()
    if as_list:
        return list(input_string[: len(input_string) - 1])
    else:
        return input_string[: len(input_string) - 1]

def invr() -> Iterator[int]:
    # This function gets a line of input from the user, splits it by spaces, and converts each of the
    # resulting parts to an integer. It returns a map object, which can be iterated over to access the integers.
    # Note:
    # You can't directly access elements from a map object by index like you can with a list. However, you can iterate
    # through the map object, or convert it into a list or tuple if necessary. Map objects can be more memory-efficient
    # than lists when dealing with large amounts of data because they generate items on-the-fly and do not need to store
    # all values in memory like lists do. However, they can only be iterated over once. After you've gone through all
    # the items in a map object, you can't go through them again without recreating the map object.
    return map(int, user_input().split())

def partitions(partitions_n: int):
    partitions_a = [0 for _ in range(partitions_n + 1)]
    k_ = 1
    y_ = partitions_n - 1
    while k_ != 0:
        x_ = partitions_a[k_ - 1] + 1
        k_ -= 1
        while 2 * x_ <= y_:
            partitions_a[k_] = x_
            y_ -= x_
            k_ += 1
        l_ = k_ + 1
        while x_ <= y_:
            partitions_a[k_] = x_
            partitions_a[l_] = y_
            yield partitions_a[: k_ + 2]
            x_ += 1
            y_ -= 1
        partitions_a[k_] = x_ + y_
        y_ = x_ + y_ - 1
        yield partitions_a[: k_ + 1]


