def Bob_sMaxStrength(pieces):
    pMaxScore = max(accumulate(pieces))
    sMaxScore = max(accumulate(pieces[::-1]))

    if pMaxScore < 0 and sMaxScore < 0:
        return -sum(piece for piece in pieces if piece < 0)

    if pMaxScore > sMaxScore:
        return pMaxScore - sum(piece for piece in pieces if piece < 0)

    return sMaxScore - sum(piece for piece in pieces if piece < 0)


