class Spider(object):
    def __init__(self, info):
        self.__nBeads, *self.__threads = info
        self.__beads = range(1, self.__nBeads+1)
        self.__connections = tuple([] for _ in self.__beads) + ([], )
        for i, j in zip(range(0, len(self.__threads), 2), range(1, len(self.__threads), 2)):
            self.__connections[self.__threads[i]].append(self.__threads[j])
            self.__connections[self.__threads[j]].append(self.__threads[i])

    def __len__(self):
        return lengthOf(self.__connections, 1+self.__nBeads-max(map(len, self.__connections)),
                        tuple(bead for bead in self.__beads if len(self.__connections[bead]) == 1))


def lengthOf(tree, maxLength, leaves):
    highestDistance = 0
    for i in range(len(leaves)-1):
        distance = furthestDistanceFrom(leaves[i], tree, leaves, set())
        if distance == maxLength:
            return distance
        if distance > highestDistance:
            highestDistance = distance

    return highestDistance


def furthestDistanceFrom(node, tree, leaves, usedNodes):
    usedNodes.add(node)
    try:
        return max(furthestDistanceFrom(neighbor, tree, leaves, usedNodes)
                   for neighbor in tree[node]
                   if neighbor not in leaves and neighbor not in usedNodes) + 1

    except ValueError:
        return 1


