        n = int(input())
        arr = [int(i) for i in input().split(" ")]

        for i in range(1, len(arr)):
            arr[i] = arr[i-1] + arr[i]

        target = arr[len(arr)-1]/2
        return (self.binary_search(arr, target)+1)


